const changeSlide = function (e) {
  if (!e.target.classList.contains("features-btn")) return;
  const currBtn = e.target;
  currBtn.classList.add("active-btn");
  const obj = features.filter((f, i) => f.name === currBtn.dataset.name)[0];

  const markup = `
  <div class="div-feature-icon">
  <!-- Scale - -->    
  ${obj.icon}
  </div>
  <div class="features-desc">
  <p class="features-title">${obj.title}</p>
  <p class="desc-text">
  ${obj.description}
  Lorem ipsum dolor sit amet, consectetur adipisicing elit. Reprehenderit, eveniet dolorum? Aspernatur autem ullam voluptatem at tenetur fugiat doloremque minima quidem nulla voluptatibus in et,
  </p>
  </div>
  `;

  descFeaturesDiv.innerHTML = markup;

  btnFeaturesDiv.childNodes.forEach((b, i) => {
    if (!b.classList) return;
    b.classList.remove("active-btn");
  });
  e.target.classList.add("active-btn");
};

btnFeaturesDiv.addEventListener("click", changeSlide);

// 3. Slider btn
btnLeft.addEventListener("click", function () {
  btnFeaturesDiv.scrollBy(-300, 0);
});

btnRight.addEventListener("click", function () {
  btnFeaturesDiv.scrollBy(300, 0);
});

