const calcRating = function (rating) {
  let markup = ``;
  for (let i = 1; i <= 5; i++) {
    if (i <= rating) {
      markup += star;
      continue;
    } else {
      console.log("hey");
      markup += noStar;
    }
  }
  return markup;
};

